mapper.update:
UPDATE products
SET product_stock = product_stock-#{orderCount}
    AND product_amount = product_amount+#{orderCount}
WHERE product_no = #{productNo}

UPDATE products
SET product_stock = product_stock+#{orderCount}
    AND product_amount = product_amount-#{orderCount}
WHERE product_no = #{productNo}

mapper.select:
SELECT user_no, account_money, account_point
FROM accounts
WHERE user_no = #{userNo}

mapper.update:
UPDATE account
SET account_money = account_money - #{accountMoney}
    AND account_point = account_point + #{accountPoint}
WHERE user_no = #{userNo}

UPDATE account
SET account_money = account_money + #{accountMoney}
    AND account_point = account_point - #{accountPoint}
WHERE user_no = #{userNo}
